import React, { useState } from 'react';
import axios from 'axios';

const Quiz = () => {
  const [questions, setQuestions] = useState([]);
  const [selectedAnswers, setSelectedAnswers] = useState({});
  const [roundId, setRoundId] = useState('');

  const getQuestions = async () => {
    const response = await axios.get(`http://127.0.0.1:8000/quiz_rounds/${roundId}/questions/`);
    setQuestions(response.data);
  };

  const handleOptionChange = (questionId, event) => {
    const selectedAnswer = event.target.value;
    setSelectedAnswers({ ...selectedAnswers, [questionId]: selectedAnswer });
  };

  const handleSaveAnswers = async () => {
    for (const questionId in selectedAnswers) {
      const selectedAnswer = selectedAnswers[questionId];
      await axios.put(`/quiz_rounds/${roundId}/questions/${questionId}`, {
        correct_answer: selectedAnswer
      });
    }
  };

  return (
    <div>
      <input type="text" placeholder="Enter round ID" onChange={event => setRoundId(event.target.value)} />
      <button onClick={getQuestions}>Get Questions</button>
      {questions.map(question => (
        <div key={question.id}>
          <h3>Question {question.question_number}</h3>
          <div>
            <input
              type="radio"
              value="A"
              checked={selectedAnswers[question.id] === 'A'}
              onChange={event => handleOptionChange(question.id, event)}
            />
            A
          </div>
          <div>
            <input
              type="radio"
              value="B"
              checked={selectedAnswers[question.id] === 'B'}
              onChange={event => handleOptionChange(question.id, event)}
            />
            B
          </div>
          <div>
            <input
              type="radio"
              value="C"
              checked={selectedAnswers[question.id] === 'C'}
              onChange={event => handleOptionChange(question.id, event)}
            />
            C
          </div>
          <div>
            <input
              type="radio"
              value="D"
              checked={selectedAnswers[question.id] === 'D'}
              onChange={event => handleOptionChange(question.id, event)}
            />
            D
          </div>
        </div>
      ))}
      <button onClick={handleSaveAnswers}>Save Answers</button>
    </div>
  );
};

export default Quiz;
